  //   switch (sortedBy) {
  //     case BaseOrderByInput.created_at_ASC:
  //       return ["product.created_at", "ASC"];
  //     case BaseOrderByInput.created_at_DESC:
  //       return ["product.created_at", "DESC"];
  //     case BaseOrderByInput.updated_at_ASC:
